  color: 'red',
  'margin-left': '5px',
};
export const errorBorder = {
  border: '1px solid #f00',
  width: '100%',
  'margin-left': '5px',
  padding: '12px 20px',
  display: 'inline-block',
  'border-radius': '4px',
  'box-sizing': 'border-box',
};
export const container = {
  width: '100%',
  display: 'flex',
  'flex-direction': 'column',
  border: '1px solid',
};

export const inputContainer = {
    width: '100%',
    display: 'flex',
    'flex-direction': 'column',
    border: '1px solid',
    'margin-left': '5px',
